import React, { Component } from 'react';
import Firebase from '../../firebase';

class Favourites extends Component {
  firebase = new Firebase();
  constructor(props) {
    super(props);

    this.state = {
        authUser: null,
        quest: null,
        qindex: 1,
        questName: null,
        questImg: null,
        questCost: 0,
    };
    this.quest = this.firebase.quests(this.state.qindex);
        this.quest.on("value", snapshot => {
            this.questName = JSON.stringify(snapshot.val().name);
            this.questName = this.questName.substring(1, this.questName.length - 1);
            this.questImg = JSON.stringify(snapshot.val().imgLink);
            this.questImg = this.questImg.substring(1, this.questImg.length - 1);
            
            this.questCost = JSON.stringify(snapshot.val().cost);
            this.setState({
                authUser: null,
                quest: this.quest,
                questName: this.questName,
                questImg: this.questImg,
                qindex: 1,
                questCost: this.questCost,
            })
            console.log(this.questName);
        });
  }


  componentDidMount() {
    this.firebase.auth.onAuthStateChanged((authUser) => {
        if (authUser) {
            this.setState({ authUser });
        } else {
            this.setState({ authUser: null });
        }
    });
}


  render () {
    return (
 
    <div id = "cardAct" className = "card">
      <img id = "questImgLink" className = "card-img" src = {this.questImg} alt ="bikingImage"/>
      <div className = "card-img-overlay">
        <h4 id = "questTitle" className = "card-title">{this.questName}</h4>
        <div className = "cost-title">
         <h5 className = "card-title">Cost</h5>
        </div>

        <h4 id = "distance" className = "card-text">12 km</h4>
      </div>
    
      <div className = "card-body">
        <div className = "row">
          <div id = "walkTime" className = "col-4 text-center"></div>
          <div id = "bikeTime" className = "col-4 text-center"></div>
          <div id = "transitTime" className = "col-4 text-center"></div>
        </div>
        <p id = "questDescription" className = "card-text"></p>
        <p id = "questTags" className = "card-text"></p>
        <p className = "card-text">Click for more info</p>
      </div>

     
    </div>
    )}
};

export default Favourites;
